    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        int n= Integer.parseInt(txtpno.getText());
        txtfee.setText((n*500)+"");
        if(checkbx.isSelected()) {
             txtwaterfee.setText((n*250+""));
             txttotal.setText( (Integer.parseInt(txtfee.getText())+Integer.parseInt(txtwaterfee.getText()))+"");
        } 
        else
        {
            txttotal.setText( Integer.parseInt(txtfee.getText())+"");
        }
        
     
    }//GEN-LAST:event_jButton1ActionPerformed

